using System.Collections;using System.Collections.Generic;using UnityEngine;namespace LGFrame.Toolkit{    public static class PoolDictionary    {        public static readonly Dictionary<string, ObjectPool> Pool = new Dictionary<string, ObjectPool>();        public static UnityComponentPool<T> GetPool<T>(string poolName) where T: Component        {            UnityComponentPool<T> pool = null;            if (Pool.ContainsKey(poolName))            {                pool = Pool[poolName] as UnityComponentPool<T>;                            }            return pool;        }    }}